import React, { useState, useEffect } from 'react';
import './login.css';
import { auth } from '../firebase/firebase';
import { signInWithEmailAndPassword, signOut } from 'firebase/auth';
import { useNavigate } from 'react-router-dom';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [userType, setUserType] = useState('blood_bank');
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const loggedInStatus = localStorage.getItem('login') === 'true';
    setIsLoggedIn(loggedInStatus);
  }, []);

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const userCredential = await signInWithEmailAndPassword(auth, email, password);
      const isAdmin = checkIfAdmin(userCredential.user); // Implement this logic
      localStorage.setItem('login', 'true');
      localStorage.setItem('userType', userType);
      localStorage.setItem('isAdmin', isAdmin.toString());
      setIsLoggedIn(true);
      if (userType === 'blood_bank') {
        navigate('/bb-dash');
      } else if (userType === 'hospital') {
        navigate('/hos-data');
      }
    } catch (error) {
      setError(error.message);
    }
  };

  const handleLogout = async () => {
    try {
      await signOut(auth);
      localStorage.setItem('login', 'false');
      localStorage.removeItem('userType');
      localStorage.removeItem('isAdmin');
      setIsLoggedIn(false);
      navigate('/');
    } catch (error) {
      setError(error.message);
    }
  };

  const checkIfAdmin = (user) => {
    // Implement your admin checking logic here
    // For example, check a specific field in user data or a role in your database
    return true; // Replace with actual check
  };

  return (
    <div className="login-container">
      {error && <div className="error-message">{error}</div>}
      <form onSubmit={isLoggedIn ? handleLogout : handleLogin}>
        <div className="form-group">
          <label>Email:</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label>Password:</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label>User Type:</label>
          <select
            value={userType}
            onChange={(e) => setUserType(e.target.value)}
            required
          >
            <option value="blood_bank">Blood Bank</option>
            <option value="hospital">Hospital</option>
          </select>
        </div>
        <button type="submit">{isLoggedIn ? 'Logout' : 'Login'}</button>
        <button type="button" onClick={() => navigate('/')} className='go-home-btn'>
          Go to Home
        </button>
      </form>
    </div>
  );
};

export default Login;
