import React, { useState, useEffect } from 'react';
import './login.css';
import { auth } from '../firebase/firebase';
import { signInWithEmailAndPassword, signOut } from 'firebase/auth';
import { useNavigate } from 'react-router-dom';
import { getFirestore, doc, getDoc } from 'firebase/firestore';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [error, setError] = useState('');
  const [role, setRole] = useState('');
  const navigate = useNavigate();
  const db = getFirestore();

  useEffect(() => {
    const loggedInStatus = localStorage.getItem('login') === 'true';
    setIsLoggedIn(loggedInStatus);
  }, []);

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const userCredential = await signInWithEmailAndPassword(auth, email, password);
      localStorage.setItem('login', 'true');
      setIsLoggedIn(true);
      console.log(email)
      // Fetch the user's role from Firestore
      const userDoc = await getDoc(doc(db, 'users', email));
      if (userDoc.exists()) {
        setRole(userDoc.data().role);
        console.log('User role:', userDoc.data().role);
        // Perform any role-based navigation or logic here
      } else {
        console.log('No such user document!');
      }

      navigate('/dashboard'); // Adjust the navigation based on your needs
    } catch (error) {
      setError(error.message);
    }
  };

  const handleLogout = async () => {
    try {
      await signOut(auth);
      localStorage.setItem('login', 'false');
      setIsLoggedIn(false);
      setRole('');
      navigate('/');
    } catch (error) {
      setError(error.message);
    }
  };

  return (
    <div className="login-container">
      {error && <div className="error-message">{error}</div>}
      <form onSubmit={isLoggedIn ? handleLogout : handleLogin}>
        <div className="form-group">
          <label>Email:</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label>Password:</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <button type="submit">{isLoggedIn ? 'Logout' : 'Login'}</button>
        <button type="button" onClick={() => navigate('/')} className='go-home-btn'>
          Go to Home
        </button>
      </form>
    </div>
  );
};

export default Login;
