import React, { useState, useEffect } from 'react';
import './login.css';
import { auth } from '../firebase/firebase';
import { signInWithEmailAndPassword, signOut } from 'firebase/auth';
import { useNavigate } from 'react-router-dom';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [userType, setUserType] = useState('blood_bank'); // default user type
  const [error, setError] = useState('');
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const user = localStorage.getItem('user');
    if (user) {
      setIsLoggedIn(true);
    }
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const userCredential = await signInWithEmailAndPassword(auth, email, password);
      localStorage.setItem('user', JSON.stringify({ email: userCredential.user.email, userType }));
      setIsLoggedIn(true);
      setError('');
    } catch (error) {
      setError(error.message);
    }
  };

  const handleLogout = async () => {
    try {
      await signOut(auth);
      localStorage.removeItem('user');
      setIsLoggedIn(false);
      setEmail('');
      setPassword('');
      setError('');
      navigate("/login"); // Optional, if you want to navigate to login page after logout
    } catch (error) {
      setError(error.message);
    }
  };

  return (
    <div className="login-container">
      <div className="login-box">
        <h2>{isLoggedIn ? 'Welcome' : 'Login'}</h2>
        {error && <p className="error">{error}</p>}
        {isLoggedIn ? (
          <button onClick={handleLogout} className="login-btn">Logout</button>
        ) : (
          <form onSubmit={handleSubmit}>
            <div className="input-box">
              <input
                type="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
              />
              <label>Email</label>
            </div>
            <div className="input-box">
              <input
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
              />
              <label>Password</label>
            </div>
            <div className="input-box">
              <select 
                value={userType} 
                onChange={(e) => setUserType(e.target.value)}
                required
              >
                <option value="blood_bank">Blood Bank</option>
                <option value="hospital">Hospital</option>
              </select>
              <label>User Type</label>
            </div>
            <button type="submit" className="login-btn">Login</button>
          </form>
        )}
      </div>
    </div>
  );
};

export default Login;
